px solid #000000;">\r\n<p>Отрицательный результат</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>N = 1</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: 1px solid #000000;">\r\n<p>ВСЕ</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Больше или равно</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>N &Aring; V = 0</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: 1px solid #000000;">\r\n<p>BLT</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Меньше</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>N &Aring; V = 1</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: 1px solid #000000;">\r\n<p>ВОТ</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Больше</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Z &Uacute; (N &Aring; V) = 1</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: 1px solid #000000;">\r\n<p>BLE</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Меньше или равно</p>\r\n</td>\r\n<td style="border: 1px solid #000000;">\r\n<p>Z &Uacute; (N &Aring; V)   = 1</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal">&nbsp;</p>\r\n<p>&nbsp;</p>');
INSERT INTO `db2_article` (`id`, `content_id`, `text`) VALUES
(37, 41, '<div class="WordSection1">\r\n<p style="text-align: center;" class="1">ПРИМЕР ПРОГРАММЫ</p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;">Ниже приведен пример программы, которая реализует следующую математическую функцию:</p>\r\n<p align="center"><img src="http://172.30.0.1/eumk/2/_images/labs/serg2/image094.gif" alt="" width="126" height="42" />,</p>\r\n<p>где a, b, c, d &ndash; переменные, принимающие значения 0&nbsp;...&nbsp;255.</p>\r\n<p>Ответ формируется в следующих регистрах:</p>\r\n<p>X &ndash; старшая часть;</p>\r\n<p>Y &ndash; младшая часть.</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n<div class="WordSection2">\r\n<p>org $8000&nbsp;&nbsp; ; адрес размещения</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; программы в памяти</p>\r\n<p>a equ 3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; инициализация</p>\r\n<p>b equ 5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; переменных</p>\r\n<p>c equ 7</p>\r\n<p>d equ 9</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldaa #a</p>\r\n<p>&nbsp;ldab #a</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std $8210&nbsp; ; a*a</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldaa #b</p>\r\n<p>&nbsp;ldab #b</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std $8220&nbsp; ; b*b</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldaa #c</p>\r\n<p>&nbsp;ldab #c</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std $8230&nbsp; ; c*c</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;xgdx</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; x = 0, d = 0</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldx #a</p>\r\n<p>&nbsp;ldab #2</p>\r\n<p>&nbsp;abx</p>\r\n<p>&nbsp;stx $8240&nbsp; ; x = a + 2</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldd $8210</p>\r\n<p>&nbsp;idiv</p>\r\n<p>&nbsp;stx $8210&nbsp;</p>\r\n<p>&nbsp;std $8250&nbsp; ; остаток от a*a/a + 2</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;xgdx</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; x = 0, d = 0</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldd $8220</p>\r\n<p>&nbsp;ldx $8240</p>\r\n<p>&nbsp;idiv</p>\r\n<p>&nbsp;stx $8220&nbsp;</p>\r\n<p>&nbsp;std $8260&nbsp; ; остаток от b*b/a + 2</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;xgdx</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; x = 0, d = 0</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldd $8230</p>\r\n<p>&nbsp;ldx $8240</p>\r\n<p>&nbsp;idiv</p>\r\n<p>&nbsp;stx $8230</p>\r\n<p>&nbsp;std $8270&nbsp; ; остаток от c*c/a + 2</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldd $8250  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; 1-й остаток в d</p>\r\n<p>&nbsp;addd $8260&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; 1-й + 2-й</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; остаток</p>\r\n<p>&nbsp;addd $8270&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; 1-й + 2-й + 3-й</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; остаток</p>\r\n<p>&nbsp;ldx $8240</p>\r\n<p>&nbsp;idiv</p>\r\n<p>&nbsp;std $8250&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; сохраняем пос-</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; ледний остаток</p>\r\n<p>&nbsp;xgdx</p>\r\n<p>&nbsp;addd $8210&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;addd $8220</p>\r\n<p>&nbsp;addd $8230</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;std tw</p>\r\n<p>&nbsp;ldaa #d</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std m2</p>\r\n<p>&nbsp;ldab tw</p>\r\n<p>&nbsp;ldaa #d</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;addb m2</p>\r\n<p>&nbsp;adca #0</p>\r\n<p>&nbsp;std m1</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldx m1</p>\r\n<p>&nbsp;ldy m2</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;stx $8290&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; старшая часть</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; результата</p>\r\n<p>&nbsp;sty $8280&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; младшая</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; часть результата</p>\r\n<p>&nbsp;ldd $8250</p>\r\n<p>&nbsp;ldaa #d</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std $8260&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; младшая часть</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; от c*d</p>\r\n<p>&nbsp;ldd $8250</p>\r\n<p>&nbsp;ldab #d</p>\r\n<p>&nbsp;mul</p>\r\n<p>&nbsp;std $8270&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; старшая часть</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; от c*d</p>\r\n<p>&nbsp;pshb</p>\r\n<p>&nbsp;pula</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;addd $8260</p>\r\n<p>&nbsp;std $8270&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; c*d</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;ldx $8240</p>\r\n<p>&nbsp;idiv</p>\r\n<p>&nbsp;std $8210&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; остаток от</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; всех остатков</p>\r\n<p>&nbsp;stx $8220&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; добавить к</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; результату</p>\r\n<p>&nbsp;ldd $8280</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;addd $8220&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; прибавка</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; точности</p>\r\n<p>&nbsp;pshb</p>\r\n<p>&nbsp;psha</p>\r\n<p>&nbsp;pulb</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;addd $8290</p>\r\n<p>&nbsp;xgdx&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; формируем</p>\r\n<p>&nbsp;pula&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; результат</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;xgdy</p>\r\n<p>&nbsp;xgdy</p>\r\n<p>&nbsp;psha</p>\r\n<p>&nbsp;xgdx</p>\r\n<p>&nbsp;pshb</p>\r\n<p>&nbsp;psha</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;psha</p>\r\n<p>&nbsp;pulx</p>\r\n<p>&nbsp;puly</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;clra</p>\r\n<p>&nbsp;clrb</p>\r\n<p>&nbsp;</p>\r\n<p>loop:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; бесконечный</p>\r\n<p>&nbsp;bra loop&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ; цикл</p>\r\n<p>&nbsp;rts</p>\r\n<p>&nbsp;</p>\r\n<p>tw dw 0</p>\r\n<p>m1 db 0</p>\r\n<p>m2 db 0</p>\r\n<p>m3 db 0</p>\r\n</div>');
